INSERT INTO `wp_snippets` VALUES (5,'Grid de Jogos RAWG','','function mostrar_jogos_rawg_customizado() {\n    // --- 1. CONFIGURAÇÕES ---\n    // Sua chave da API (copiei do seu print)\n    $api_key = \'428bf9bc5a684ba2a72b51374385c55e\'; \n    \n    // Pega o número da página atual da URL (se não tiver, é 1)\n    $pagina_atual = isset($_GET[\'pg\']) ? (int)$_GET[\'pg\'] : 1;\n    if ($pagina_atual < 1) $pagina_atual = 1;\n\n    // --- 2. CONEXÃO DIRETA (wp_remote_get) ---\n    // Montamos a URL na mão. Isso garante que cada página tenha sua URL única.\n    $url = \"https://api.rawg.io/api/games?key={$api_key}&page={$pagina_atual}&page_size=12\";\n\n    // O WordPress vai buscar os dados (sem passar pelo plugin WPGetAPI)\n    $response = wp_remote_get($url);\n\n    // Verificamos se houve erro na conexão (ex: site da RAWG fora do ar)\n    if ( is_wp_error($response) ) {\n        return \'<div style=\"color:red\">Erro ao conectar com a API.</div>\';\n    }\n\n    // Pegamos o corpo da resposta e transformamos em Lista (Array)\n    $body = wp_remote_retrieve_body($response);\n    $data = json_decode($body, true);\n\n    // --- 3. EXIBIÇÃO (HTML/CSS) ---\n    // Daqui para baixo, a lógica é a mesma de antes\n    if ( isset($data[\'results\']) && !empty($data[\'results\']) ) {\n        \n        // Adicionei o ID \"lista-jogos\" para a âncora funcionar\n        $output = \'<div id=\"lista-jogos\" class=\"rawg-games-grid\">\';\n\n        foreach ( $data[\'results\'] as $game ) {\n            $image_url = isset($game[\'background_image\']) ? $game[\'background_image\'] : \'\';\n            $name = isset($game[\'name\']) ? $game[\'name\'] : \'Sem nome\';\n            \n            if (empty($image_url)) {\n                $style = \'style=\"background-color: #333; min-height: 160px; display: flex; align-items: center; justify-content: center;\"\';\n                $img_tag = \'<span style=\"color: #777;\">Sem Imagem</span>\';\n            } else {\n                $style = \'\';\n                $img_tag = \'<img src=\"\' . esc_url($image_url) . \'\" alt=\"\' . esc_attr($name) . \'\">\';\n            }\n\n            $output .= \'<div class=\"rawg-card\">\';\n            $output .= \'<div class=\"rawg-card-image\" \' . $style . \'>\' . $img_tag . \'</div>\';\n            $output .= \'<div class=\"rawg-card-content\">\';\n            $output .= \'<h3>\' . esc_html($name) . \'</h3>\';\n            $output .= \'</div>\'; \n            $output .= \'</div>\'; \n        }\n\n        $output .= \'</div>\'; // Fecha grid\n\n        // --- 4. PAGINAÇÃO ---\n        $output .= \'<div class=\"rawg-pagination\">\';\n        \n        // Botão Anterior\n        if ($pagina_atual > 1) {\n            $prev = $pagina_atual - 1;\n            // O #lista-jogos faz a tela rolar para o topo da lista ao clicar\n            $output .= \'<a href=\"?pg=\' . $prev . \'#lista-jogos\" class=\"rawg-btn\">« Anterior</a>\';\n        }\n\n        $output .= \'<span class=\"rawg-page-number\">Página \' . $pagina_atual . \'</span>\';\n\n        // Botão Próximo\n        if ( isset($data[\'next\']) && !empty($data[\'next\']) ) {\n            $next = $pagina_atual + 1;\n            $output .= \'<a href=\"?pg=\' . $next . \'#lista-jogos\" class=\"rawg-btn\">Próximo »</a>\';\n        }\n        \n        $output .= \'</div>\';\n\n        return $output;\n\n    } else {\n        return \'<div style=\"color: white; padding: 20px;\">Nenhum jogo encontrado.</div>\';\n    }\n}\n\nadd_shortcode(\'meus_jogos_rawg\', \'mostrar_jogos_rawg_customizado\');','','global',0,10,1,'2025-11-25 17:39:23',1,NULL);
