INSERT INTO `wp_posts` VALUES (198,1,'2025-11-25 16:34:52','2025-11-25 16:34:52','/* Corrige a cor das setas do calendário Eventin */\n.etn-default-calendar-style .fc-button {\n    background-color: transparent !important;\n    border: 1px solid #ddd !important;\n    border-radius: 50% !important;\n    color: #f9f9f9 !important; /* Cor das setas */\n    display: grid !important;\n    height: 40px;\n    padding: 0;\n    place-items: center !important;\n    text-align: center;\n    width: 40px;\n}\n\n/* Caso seja necessário alterar somente o ícone da seta */\n.etn-default-calendar-style .fc-button .fc-icon {\n    color: #f9f9f9 !important; \n}\n\n/* Grade Container */\n.rawg-games-grid {\n    display: grid;\n    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));\n    gap: 20px;\n    padding: 20px 0;\n}\n\n/* O Card Individual */\n.rawg-card {\n    background: #202020; /* Cor de fundo do card */\n    border-radius: 10px;\n    overflow: hidden;\n    transition: transform 0.3s ease;\n    box-shadow: 0 4px 6px rgba(0,0,0,0.3);\n}\n\n.rawg-card:hover {\n    transform: translateY(-5px);\n    box-shadow: 0 8px 15px rgba(0,0,0,0.5);\n}\n\n/* A Imagem */\n.rawg-card-image {\n    height: 160px; /* Altura fixa para alinhar tudo */\n    width: 100%;\n    overflow: hidden;\n}\n\n.rawg-card-image img {\n    width: 100%;\n    height: 100%;\n    object-fit: cover; /* Garante que a imagem preencha sem distorcer */\n}\n\n/* O Título */\n.rawg-card-content {\n    padding: 15px;\n}\n\n.rawg-card-content h3 {\n    color: #fff;\n    font-size: 18px;\n    margin: 0;\n    font-family: sans-serif; /* Ajuste para a fonte do seu site */\n    white-space: nowrap;\n    overflow: hidden;\n    text-overflow: ellipsis; /* Adiciona \"...\" se o nome for muito longo */\n}\n\n    color: #0B0E13 !important;\n}\n\n/* Mantém contêineres internos em branco */\n.page-id-419 .site,\n.page-id-419 .content-area,\n.page-id-419 .etn-default-calendar-style,\n.page-id-419 .etn-event-calendar-wrapper {\n    background: #ffffff !important;\n}\n\n/* Se houver seções sobrepondo o fundo */\n.page-id-419 section,\n.page-id-419 .elementor-section {\n    background: #ffffff !important;\n}\n\n\n\n/* Container da Paginação */\n.rawg-pagination {\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    gap: 20px;\n    margin-top: 30px;\n    padding-bottom: 20px;\n}\n\n/* Os Botões (Anterior / Próximo) */\n.rawg-btn {\n    background-color: #333; /* Cor do fundo do botão */\n    color: #fff;            /* Cor do texto */\n    padding: 10px 20px;\n    text-decoration: none;\n    border-radius: 5px;\n    border: 1px solid #444;\n    transition: all 0.3s ease;\n    font-weight: bold;\n}\n\n.rawg-btn:hover {\n    background-color: #00ce70; /* Cor verde (exemplo) ao passar o mouse */\n    color: #000;\n    border-color: #00ce70;\n}\n\n/* O número da página no meio */\n.rawg-page-number {\n    color: #aaa;\n    font-size: 14px;\n}\n','bame-child','','inherit','closed','closed','','161-revision-v1','','','2025-11-25 16:34:52','2025-11-25 16:34:52','',161,'http://playdex.local/?p=198',0,'revision','',0);
